import React, { useContext } from "react";
import { DentistContext } from "../Routes/Home";
import Card from "../Components/Card";

const Favs = () => {
  const { dentists } = useContext(DentistContext);
  const favorites = JSON.parse(localStorage.getItem("favorites")) || [];
  const filteredDentists = dentists.filter(dentist =>
    favorites.some(favorite => favorite.id === dentist.id))

  return (
    <div>
      <h2>Favorite Dentists</h2>
      {filteredDentists.length > 0 ? (
        filteredDentists.map(dentist => <Card key={dentist.id} {...dentist} />)
      ) : (
        <p>No favorite dentists yet.</p>
      )}
    </div>
  );
};

export default Favs;
