import React, { useContext } from 'react';
import { DentistContext } from '../Routes/Home';
import '../index.css';

const Card = ({ name, username, id }) => {

  const { dentists } = useContext(DentistContext);

  const addFav = (dentist)=>{
    // Aqui iria la logica para agregar la Card en el localStorage

    const favoriteDentists = JSON.parse(localStorage.getItem('favoriteDentists')) || [];
    favoriteDentists.push(dentist);
    localStorage.setItem('favoriteDentists', JSON.stringify(favoriteDentists));
  }

  return (
    <div className="card">
           {dentists.map(dentist => (
        <div key={dentist.id}>
          <h2>{dentist.name}</h2>
          <p>{dentist.email}</p>
          <p>{dentist.phone}</p>
          <button onClick={addFav} className="favButton">Add fav</button>
        </div>
      ))}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}

    </div>
  );
};

export default Card;
