import React, { useContext, useState} from 'react';
import { DentistContext } from '../Routes/Home';
import '../index.css';

const Card = ({ name, email, phone }) => {

  const { dentists } = useContext(DentistContext);

  const [isFav, setIsFav] = useState(false);

  const addFav = (dentist)=>{
    // Aqui iria la logica para agregar la Card en el localStorage

    const currentFavs = JSON.parse(localStorage.getItem("favorites")) || [];
    const newFavs = [...currentFavs, dentist];
    localStorage.setItem("favorites", JSON.stringify(newFavs));
    setIsFav(true);
    console.log(JSON.parse(localStorage.getItem("favorites"))); 
  }

  return (
    <div className="card">
    {dentists.map((dentist, index) => (
      <div key={index}>
        <h2>{dentist.name}</h2>
        <p>{dentist.email}</p>
        <p>{dentist.phone}</p>
        <button onClick={() => addFav(dentist)}>Agregar a favoritos</button>
      </div>
    ))}
  </div>
  );
};

export default Card;
