import React, { useContext, useState} from 'react';
import { DentistContext } from '../Routes/Home';
import '../index.css';

const Card = ({ name, email, phone }) => {

  const { dentists } = useContext(DentistContext);

  const [isFav, setIsFav] = useState(false);

  const addFav = (dentist)=>{
    // Aqui iria la logica para agregar la Card en el localStorage

    const currentFavs = JSON.parse(localStorage.getItem("favorites")) || [];
    const newFavs = [...currentFavs, dentist];
    localStorage.setItem("favorites", JSON.stringify(newFavs));
    setIsFav(true);
    console.log(JSON.parse(localStorage.getItem("favorites"))); 
  }

  return (
    <div className="card">
           {dentists.map(dentist => (
        <div key={dentist.id}>
          <h2>{dentist.name}</h2>
          <p>{dentist.email}</p>
          <p>{dentist.phone}</p>
          <button onClick={addFav}>{isFav ? "Added to favorites" : "Add to favorites"}</button>

        </div>
      ))}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}

    </div>
  );
};

export default Card;
